values.scoreCalc:
// Draw
	if(${Player1.response[0]}===${Player2.response[0]}) {
		return 0
	}
	// Paper Rock
	if(${Player1.response[0]}==="Paper" && ${Player2.response[0]} === "Rock") {
		return ${currentRole} === "Player1" ? 1 : 0
	}
	if(${Player1.response[0]}==="Rock" && ${Player2.response[0]} === "Paper") {
		return ${currentRole} === "Player1" ? 0 : 1
	}
	// Paper Scissors
	if(${Player1.response[0]}==="Paper" && ${Player2.response[0]} === "Scissors") {
		return ${currentRole} === "Player1" ? 0 : 1
	}
	if(${Player1.response[0]}==="Scissors" && ${Player2.response[0]} === "Paper") {
		return ${currentRole} === "Player1" ? 1 : 0
	}
	// Rock Scissors
	if(${Player1.response[0]}==="Scissors" && ${Player2.response[0]} === "Rock") {
		return ${currentRole} === "Player1" ? 0 : 1
	}
	if(${Player1.response[0]}==="Rock" && ${Player2.response[0]} === "Scissors") {
		return ${currentRole} === "Player1" ? 1 : 0
	}

// Cooperate Cooperate
	if(${Player1.response[0]}==="Cooperate" && ${Player2.response[0]} === "Cooperate") {
		return 2
	}
	// Defect Defect
	if(${Player1.response[0]}==="Paper" && ${Player2.response[0]} === "Scissors") {
		return 1
	}
	// Cooperate Defect
	if(${Player1.response[0]}==="Cooperate" && ${Player2.response[0]} === "Defect") {
		return ${currentRole} === "Player1" ? 0 : 3
	}
	if(${Player1.response[0]}==="Defect" && ${Player2.response[0]} === "Cooperate") {
		return ${currentRole} === "Player1" ? 3 : 0
	}
